function getBits(value, offset, len) {
        var mask = 0;
        while (len > 0) {
                mask = mask << 1;
                mask++;
                len--;
        }
        return ((value >> offset) & mask);
}

function MAX_ParseDateTime(byte1, byte2, byte3) {
	var result = {};
	result.day = byte1 & 0x1F;
	result.month = ((byte1 & 0xE0) >> 4) | (byte2 >> 7);
	result.year = byte2 & 0x3F;
	var time = byte3 & 0x3F;
	if ((time % 2) > 0)
		result.time = (time / 2) + ":30";
	else
		result.time = (time / 2) + ":00";

	result.str = result.day+"."+result.month+"."+result.year+" "+result.time;
	return result;
}

var ctrl_modes = { 
	'0' : "auto",
	'1' : "manual",
	'2' : "temporary",
	'3' : "boost",
}; 

var obj = msg.payload;
var data = msg.payload.payload;

var bits2 = parseInt( Number("0x"+data.substr(0,2)) , 10 );

obj.valveposition = parseInt( Number("0x"+data.substr(2,2)) , 10 );
obj.desiredTemperatureRaw = parseInt( Number("0x"+data.substr(4,2)) , 10 );

var until1 = parseInt( Number("0x"+data.substr(6,2)) , 10 );
var until2 = parseInt( Number("0x"+data.substr(8,2)) , 10 );
var until3 = parseInt( Number("0x"+data.substr(10,2)) , 10 );

obj.mode = getBits(bits2, 0, 2);
obj.modeStr = ctrl_modes[obj.mode] ? ctrl_modes[obj.mode] : obj.mode;
obj.dstsetting = getBits(bits2, 3, 1); //is automatically switching to DST activated
obj.langateway = getBits(bits2, 4, 1); //??
obj.panel = getBits(bits2, 5, 1); //1 if the heating thermostat is locked for manually setting the temperature at the device
obj.rferror = getBits(bits2, 6, 1); //communication with link partner (what does that mean?)
obj.batterylow = getBits(bits2, 7, 1); //1 if battery is low
obj.battery = obj.batterylow ? "low" : "ok";

if (until3 > 0) data.untilStr = MAX_ParseDateTime(until1, until2, until3);
obj.measuredTemperature = (until2 > 0) ? (((until1 & 0x01)<<8) + until2) : 0;
//If the control mode is not "temporary", the cube sends the current (measured) temperature
if ((obj.mode == 2) || (obj.measuredTemperature == 0)) obj.measuredTemperature = null;
if (obj.mode != 2) delete obj.untilStr;



return msg;

function getBits(value, offset, len) {
        var mask = 0;
        while (len > 0) {
                mask = mask << 1;
                mask++;
                len--;
        }
        return ((value >> offset) & mask);
}

var obj = msg.payload;
var data = msg.payload.payload;

var bits = ("0x"+data.substr(0,2))*1;
obj.isopen = (getBits(bits, 0, 2) === 0) ? 0 : 1;
obj.unkbits = getBits(bits, 2, 4);
obj.rferror = getBits(bits, 6, 1);
obj.batterylow = getBits(bits, 7, 1);
obj.battery = data.batterylow ? "low" : "ok";

msg.payload = obj;		
return msg;

function hex_to_ascii(str1)
 {
	var hex  = str1.toString();
	var str = '';
	for (var n = 0; n < hex.length; n += 2) {
		str += String.fromCharCode(parseInt(hex.substr(n, 2), 16));
	}
	return str;
 }

