var ctrl_modes = { 
	'0' : "auto",
	'1' : "manual",
	'2' : "temporary",
	'3' : "boost",
}; 

var obj = msg.payload;
var data = obj.payload;


var bits =  ("0x"+data.substr(0,2))*1;
msg.payload.mode = bits >> 6;
msg.payload.modeStr = ctrl_modes[msg.payload.mode] ? ctrl_modes[msg.payload.mode] : msg.payload.mode;
msg.payload.desiredTemperature = (bits & 0x3F) / 2.0; // Convert to degree celcius.


return msg;

var obj = msg.payload;
var data = obj.payload;

obj.year = 2000+ ("0x"+data.substr(0,2))*1;
obj.day =  ("0x"+data.substr(2,2))*1;
obj.hour =  (("0x"+data.substr(4,2))*1 ) & 0x1F;
obj.min =  (("0x"+data.substr(6,2))*1) & 0x3F;
obj.sec = (("0x"+data.substr(8,2))*1) & 0x3F;
obj.month = (((("0x"+data.substr(6,2))*1) >> 6) << 2) | ((("0x"+data.substr(8,2))*1) >> 6); // this is just guessed according to FHEM
obj.unk1 = (("0x"+data.substr(4,2))*1 ) >> 5;
obj.unk2 = (("0x"+data.substr(6,2))*1 ) >> 6;
obj.unk3 = (("0x"+data.substr(8,2))*1 ) >> 6;

return msg;

var objIn = msg.payload;

var emptyCulfw = {
    Name: "",
    LastMessage: 0,
    TimeOut: -1,
    BusyWith: -1,
    Step: -1
