/*  PairPing received 
    sent a PairPong or Reset if paired with another DeviceId

    output 1 -> send PairPong
    output 2 -> send Reset

 */
var config = global.get("culfw_config");

var culfw = msg.culfw;

// The PairPing message has an ascii id of this device
// Add it to the culfw object
culfw.serial = msg.payload.serial;

var msg1 = null; // pairpong output
var msg2 = null; // reset output

// if dest != "000000" and not our id: this device is still paired with a different Culfw or Cube
if( msg.payload.dest != config.DeviceId && msg.payload.dest != "000000"){
    culfw.Step = 1;                 // wacht op Ack

    // sent this reset in name of someone else.
    msg2 = {
            sender: msg.payload.dest,  
            dest: msg.payload.deviceId
    };

}
else{
    culfw.Step = 1;                 // wacht op Ack
    msg1 = msg;
}


global.set( culfw.Name , culfw);

return [msg1,msg2];

/*  Build een bericht dat verzonden kan worden.

    indien msg.sender gedefinieerd is, moet dit als verzender gebruikt worden.
        (reset van aan ander toestel gebonden apparaat)

    msg.payload is een object met volgende properties:

msgType - msg type raw
dest    - destination address (3byte - 000000 for broadcast)
groupId - groep id
payload - payload...

Z  0E E0 05 40 17D0C0 137BFC 04 63 000000
Zs 0E 00 00 40 012345 137A0A 00 6C 000000

Zs0F000040012345137A0A006C000000

1ste byte= lengte -> wordt hier berekend
2de byte = msgCounter -> voorlopig 0
3de byte flag - control byte (flag ?) -> voorlopig 0
5de parameter = sender  - sender address (3byte) -> dit toestel, voorlopig vast.

*/

var config = global.get("culfw_config");

var obj = msg.payload;
var mySelf = "";
if( msg.sender === undefined || msg.sender === null){
    mySelf = config.DeviceId;
}else{
    mySelf = msg.sender;
}


msgCounter = "00";
flag = "00";

var bericht = msgCounter + flag + obj.msgType + mySelf + obj.dest + obj.groupId + obj.payload;

var len = (bericht.length / 2 + 0).toString(16);
if( len.length == 1 ){
    len = "0"+len;
}

msg.payload = "Zs"+len.toString(16).toUpperCase()+bericht.toUpperCase();

return msg;

// einde vd taken.

var culfw = msg.culfw;

// end of this action
culfw.BusyWith = -1;    
culfw.Step = -1;
global.set( culfw.Name , culfw);

return msg;

msg.payload = msg.payload + '\r\nZr\r\n';
msg._session = null;    
return msg;

var temp = msg.payload;

/*
ll - length
nn - msg counter
cc - control byte (flag ?)
tt - msg type
ss - sender address (3byte)
dd - destination address (3byte - 000000 for broadcast)
gg - groep id
pp - payload...

Z  0E E0 05 40 17D0C0 137BFC 04 63 000000
Zs 0E 00 00 40 012345 137A0A 00 6C 000000

Zs0F000040012345137A0A006C000000

*/
var culfwNaam = "culfw_137a0a";
var culfw = context.get(culfwNaam) || {};

// tijd wordt hier altijd aangepast
culfw.LastMessage = Date.now();
msg.culfw = culfw;

//-------------------------------------------------------
var bits = (0x40 | (temp*2 & 0x3F)).toString(16);
var extra = "000000";

var obj = {};

obj.msgType = "40";             // pairpong
obj.dest = "137a0a";
obj.groupId = "00";
obj.payload = bits + extra;
culfw.Stap = 1;                 // wacht op Ack

msg.payload = obj;
return msg;

var okToRcv = context.get("onoff");
if( okToRcv === undefined )
    okToRcv = true;

if(msg.topic == "false" ){
    okToRcv = false;
    context.set("onoff" , okToRcv )
}
if(msg.topic == "true" ){
    okToRcv = true;
    context.set("onoff" , okToRcv )
}

if( ! okToRcv || msg.topic.length > 0 ){
    return null;
}

msg.topic = '';

return msg;

var obj = {};

obj.msgType = "01";             // pairpong
obj.dest = msg.payload.deviceId;
obj.groupId = "00";
obj.payload = "00";

msg.payload = obj;

return msg;

/* RESET

    msg.sender = send in name of...
    msg.dest = destination 

*/
obj = {};
obj.msgType = "F0";             // reset
obj.dest = msg.dest;
obj.sender = msg.sender;
obj.groupId = "00";
obj.payload = "00";

msg.payload = obj;

return msg;

// messages need to be separated from each other when sending.
// this queue holds messages.
//
// if a message arrives for the same device with the same message id, 
// than an existing message, the old message is replaced with the new.
//----------------------------------------------------------------------
var queue = context.get("queue") || [];

if( typeof(msg.payload) != 'boolean' ){
    var obj = msg.payload;
    var ix = -1;
    for( i = 0 ; i < queue.length; i++){
        var el = queue[i];
        if( el.culfw.Name == obj.culfw.Name 
            && el.culfw.BusyWith == obj.culfw.BusyWith
        ){
            ix = i;
            break;
        }
    }
    if( ix >= 0){
        queue[ix] = obj;
    }else{
        queue.push(obj);
    }
    // send empty message
    msg = null;
}
else{
    if( queue.length > 0){
        msg = {payload: queue.shift() };
    }else{
        msg = null;
    }
}

context.set("queue",queue);

return msg;

